UseCommands = true -- Allow the use of /commands
DisableSeatShuffle = true -- Allow auto-seat shuffle from passenger to driver
LeaveRunning = true -- Allow the engine to keep running on exit of the vehicle with a long F keypress
DefaultOpen = 'HOME' -- Key to open menu https://docs.fivem.net/docs/game-references/input-mapper-parameter-ids/keyboard/, client can change in the game menu (Settings->Key Binds->FiveM)